package main
import (
    "fmt"
)

func main() {
    var primes []int
    for i := 1000; ; i++ {
        isPrime := true
        for j := 2; j*j <= i; j++ {
            if i%j == 0 {
                isPrime = false
                break
            }
        }
        if isPrime {
