<!DOCTYPE html>
<html>
<head>
  <title>Canvas Drawing</title>
  <style>
    canvas {
      border: 1px solid black;
    }
  </style>
</head>
<body>
  <canvas id="canvas" width="400" height="200"></canvas>
  <script>
    let canvas = document.getElementById("canvas");
    let ctx = canvas.getContext("2d");
    let lastMousePosition = { x: 0, y: 0 };
    let tool = "brush";
    let lastStroke = [];

    function draw() {
      ctx.beginPath();
      ctx.lineCapable = true;
      ctx.lineWidth = 5;
      ctx.lineJoin = "round";
    }

    function drawLine(x, y) {
      ctx.lineTo(x, y);
      lastStroke.push({ x, y });
    }

    function mouseDown(x, y) {
      lastMousePosition = { x, y };
      draw();
    }

    function mouseMove(x, y) {
      let dx = x - lastMousePosition.x;
      let dy = y - lastMousePosition.y;
      lastMousePosition = { x, y };
      drawLine(lastMousePosition.x + dx, lastMousePosition.y + dy);
    }

    function mouseUp() {
      lastStroke = [];
    }

    function mousemove(x, y) {
      mouseMove(x, y);
    }

    function mousedown(x, y) {
      mouseDown(x, y);
    }

    function mouseup() {
      mouseUp();
    }

    canvas.addEventListener("mousedown", mousedown);
    canvas.addEventListener("mousemove", mousemove);
    canvas.addEventListener("mouseup", mouseup);

    function draw() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      for (let i = 0; i < lastStroke.length; i++) {
        ctx.beginPath();
        ctx.moveTo(lastStroke[i].x, lastStroke[i].y);
        ctx.lineTo(lastStroke[i].x, lastStroke[i].y);
      }
      ctx.stroke();
    }

    draw();
  </script>
</body>
</html>
