```go
package main
import (
    "fmt"
    "math"
)

func isPrime(n int) bool {
    if n <= 1 {
        return false
    }
    for i := 2; i <= int(math.Sqrt(float64(n))); i++ {
        if n%i == 0 {
            return false
        }
    }
    return true
}

func main() {
    var primes []int
    for i := 1000; i <= 2000; i++ {
        if isPrime(i) {
            primes = append(primes, i)
        }
    }
    if len(primes) < 3 {
        fmt.Println("There are not enough prime numbers in the range.")
        return
    }
    product := 1
    for _, prime := range primes[:3] {
        product *= prime
    }
    fmt.Printf("The product of %d, %d, and %d, which are all prime numbers, is %d.\n", primes[0], primes[1], primes[2], product)
}
