Here is a simple JavaScript program that allows users to draw on an HTML5 canvas:
```
// Get the canvas element
var canvas = document.getElementById('canvas');
var ctx = canvas.getContext('2d');

// Set the canvas dimensions
canvas.width = 400;
canvas.height = 400;

// Initialize the drawing variables
var isDrawing = false;
var lastX, lastY;
var brushSize = 5;
var brushColor = 'black';

// Add event listeners for mouse and touch events
canvas.addEventListener('mousedown', function(e) {
  isDrawing = true;
  lastX = e.offsetX;
  lastY = e.offsetY;
});

canvas.addEventListener('mousemove', function(e) {
  if (isDrawing) {
    ctx.beginPath();
    ctx.moveTo(lastX, lastY);
    ctx.lineTo(e.offsetX, e.offsetY);
    ctx.strokeStyle = brushColor;
    ctx.lineWidth = brushSize;
    ctx.stroke();
    lastX = e.offsetX;
    lastY = e.offsetY;
  }
});

canvas.addEventListener('mouseup', function() {
  isDrawing = false;
});

canvas.addEventListener('touchstart', function(e) {
  isDrawing = true;
  lastX = e.touches[0].clientX;
  lastY = e.touches[0].clientY;
});

canvas.addEventListener('touchmove', function(e) {
  if (isDrawing) {
    ctx.beginPath();
    ctx.moveTo(lastX, lastY);
    ctx.lineTo(e.touches[0].clientX, e.touches[0].clientY);
    ctx.strokeStyle = brushColor;
    ctx.lineWidth = brushSize;
    ctx.stroke();
    lastX = e.touches[0].clientX;
    lastY = e.touches[0].clientY;
  }
});

canvas.addEventListener('touchend', function() {
  isDrawing = false;
});

// Add event listeners for brush size and color changes
document.getElementById('brush-size').addEventListener('change', function() {
  brushSize = parseInt(this.value);
});

document.getElementById('brush-color').addEventListener('change', function() {
  brushColor = this.value;
});
