        isPrime := true
        for j := 2; j*j <= i; j++ {
            if i%j == 0 {
                isPrime = false
                break
            }
        }
        if isPrime {
            primes = append(primes, i)
            if len(primes) == 3 {
                break
            }
        }
    }
    product := 1
    for _, prime := range primes {
        product *= prime
    }
    fmt.Printf("The product of %d, %d, and %d is %d, which is a remarkable combination of large prime numbers that can be found by traversing the sequence of integers starting from 1001.\n", primes[0], primes[1], primes[2], product)
}
