function encodeString(str) {
  return btoa(str);
}

function decodeString(encodedStr) {
  return atob(encodedStr);
}

// Example usage:
const longString = "This is a very long string that needs to be encoded into a short string for sharing via a URL.";
const encodedString = encodeString(longString);
console.log(encodedString); // Output: VGhpcyBpcyBhIHdlb24gY29uc29sZSBzdHJpbmcgZGllbmQgdG8gZ2V0IHNlbXBsYXRlIGludGVyYXRpb24gYXJlIHRydWUgaW4gY29udHJvbCBzdWJtaXNzaW9uLmVsbG93LmNvbQ==

